<?php

namespace Payabli\Types;

use Payabli\Core\Json\JsonSerializableType;
use Payabli\Core\Json\JsonProperty;
use Payabli\Core\Types\ArrayType;

class NotificationContent extends JsonSerializableType
{
    /**
     * @var ?value-of<NotificationContentEventType> $eventType The notification's event name.
     */
    #[JsonProperty('eventType')]
    public ?string $eventType;

    /**
     * Indicate the format of report file to be generated by the engine.
     * Used for `method` = *report-email* and *report-web*.
     *
     * @var ?value-of<NotificationContentFileFormat> $fileFormat
     */
    #[JsonProperty('fileFormat')]
    public ?string $fileFormat;

    /**
     * @var ?array<KeyValueDuo> $internalData Array of pairs key:value to insert in request body to target in **method** = *web* or *report-web*.
     */
    #[JsonProperty('internalData'), ArrayType([KeyValueDuo::class])]
    public ?array $internalData;

    /**
     * @var ?value-of<NotificationContentReportName> $reportName The kind report to generate. For [automated reports](/developers/developer-guides/notifications-and-webhooks-overview#automated-reports) only.
     */
    #[JsonProperty('reportName')]
    public ?string $reportName;

    /**
     * @var ?int $timeZone
     */
    #[JsonProperty('timeZone')]
    public ?int $timeZone;

    /**
     * @var ?string $transactionId Used internally to reference the entity or object generating the event.
     */
    #[JsonProperty('transactionId')]
    public ?string $transactionId;

    /**
     * @var ?array<KeyValueDuo> $webHeaderParameters Array of pairs key:value to insert in header of request to target in **method** = *web* or *report-web*.
     */
    #[JsonProperty('webHeaderParameters'), ArrayType([KeyValueDuo::class])]
    public ?array $webHeaderParameters;

    /**
     * @param array{
     *   eventType?: ?value-of<NotificationContentEventType>,
     *   fileFormat?: ?value-of<NotificationContentFileFormat>,
     *   internalData?: ?array<KeyValueDuo>,
     *   reportName?: ?value-of<NotificationContentReportName>,
     *   timeZone?: ?int,
     *   transactionId?: ?string,
     *   webHeaderParameters?: ?array<KeyValueDuo>,
     * } $values
     */
    public function __construct(
        array $values = [],
    ) {
        $this->eventType = $values['eventType'] ?? null;
        $this->fileFormat = $values['fileFormat'] ?? null;
        $this->internalData = $values['internalData'] ?? null;
        $this->reportName = $values['reportName'] ?? null;
        $this->timeZone = $values['timeZone'] ?? null;
        $this->transactionId = $values['transactionId'] ?? null;
        $this->webHeaderParameters = $values['webHeaderParameters'] ?? null;
    }

    /**
     * @return string
     */
    public function __toString(): string
    {
        return $this->toJson();
    }
}
